String.prototype.toDecimal=function(){for(var t=1,r=this.length,o=this.charCodeAt(0);r>t;t++)o+=" "+this.charCodeAt(t);return o},String.prototype.fromDecimal=function(){for(var t,r=this.split(" "),o=0,n="";t=r[o];o++)n+=String.fromCharCode(t);return n},String.prototype.toHex=function(){for(var t=1,r=this.length,o=this.charCodeAt(0).toString(16);r>t;t++)o+=" "+this.charCodeAt(t).toString(16);return o},String.prototype.fromHex=function(){for(var t,r=this.split(" "),o=0,n="";t=r[o];o++)n+=String.fromCharCode(parseInt(t,16));return n},String.prototype.toBinary=function(){for(var t=1,r=this.length,o=this.charCodeAt(0).toString(2);r>t;t++)o+=" "+this.charCodeAt(t).toString(2);return o},String.prototype.fromBinary=function(){for(var t,r=this.split(" "),o=0,n="";t=r[o];o++)n+=String.fromCharCode("0b"+t);return n},String.prototype.toEntity=function(){for(var t=0,r=this.length,o="";r>t;t++)o+="&#"+this.charCodeAt(t)+";";return o},String.prototype.fromEntity=function(){return this.replace(/&#(\d+);/g,function(t,r){return String.fromCharCode(r)})},String.prototype.mirror=function(){for(var t=this.length-1,r="";t>-1;t--)r+=this[t];return r},String.prototype.capitalize=function(){return this.replace(/(^|\s)([a-z])/gm,function(t,r,o){return r+o.toUpperCase()})},String.prototype.protoTrim=function(t){var r=/^\s+|\s+$/g;if(t)switch(t=t.toLowerCase()){case"left":r=/^\s+/;break;case"right":r=/\s+$/}return this.replace(r,"")},String.prototype.toNode=function(t){var r=document.createElement(t?t:"P");return r.innerHTML=this,r},String.prototype.toTextNode=function(){return document.createTextNode(this)};
